import { useState } from 'react'
import '../Slider/index.css'
 //import logements from '../../data/logement.json'

function Slider ({slides}) {

    const [current, setCurrent] = useState(0)
    const length = slides.length

    const nextImage = () => {
        setCurrent(current === length - 1 ? 0 : current + 1); // on repart au premier slide quand on arrive au dernier
      };
      /**Function pour l'image suivante */
      const prevImage = () => {
        setCurrent(current === 0 ? length - 1 : current - 1); // on repart au dernier slide quand on est au premier
      };


    if(!Array.isArray(slides) || slides.length <= 0) {
        return null
    }

    return (
        <section className="slider">
        {/* {length >1 && (<p className="next-arow" ></p>)} */}
        {slides.map((image, index ) => {
             return(
                 <div key= {index} className={index === current ? "slider active" : "slider"}>
                    {index === current && ( <img src= {image} alt="apparment-img" className="slide-image"/>)}
                    {length > 1 && (
        <p className="right-Arrow" onClick={nextImage}>
          <i className="fa-solid fa-chevron-right"></i>
        </p>
      )}
                 </div>
             )
        })}
        </section>
    )
}

export default Slider


