import banner from '../../assets/IMG.png'
import '../Banner/index.css'
import descriptions from'../../data/description.json'



function Banner({id}) {


    return (
        <div className="banner">
        <img className="banner-image" src={ banner } alt="banner home page" />
        { descriptions.map((item) => {
          return (
            <h1 className="banner-text" key={item.id}> {item.id[5]}</h1>
          )
        })}

        </div>
    )
}

export default Banner
