import { useState } from 'react'
import '../Slider/index.css'
// import logements from '../../data/description.json'

function Slider ({pictures}) {

    const [current, setCurrent] = useState(0)
    const length = pictures.length

    // const nextSlide = () => {
    //     setCurrent(current === length - 1 ? 0 : current + 1)
    // }
    // const prevSlide = () => {
    //     setCurrent(current === 0? length - 1: current +1)
    // }

    if (!Array.isArray(pictures) || pictures.length <= 0) {
        return null;
      }

return (
    <div className="slider">
    {pictures.map((picture, index) => {
        return (
            <div
            key = {index}
            className= {index === current ? "slider acive" : "slider"}>
            {index === current && (
                <img src={picture} alt=""  className="slide-image"/>)}
            {index === current && length > 1 && ( <span className="slider-number"> { current + 1}/{ length } </span>)}

            </div>
        )
    }
    )}

    <p> my slider right here</p>
    </div>
)

}

export default Slider
